import React, { useState, useEffect, useCallback, useRef } from "react";

import Axios from "axios";
import jwt from "jwt-decode";
import { isEqual } from "lodash";

import Loader from "Assets/loader/Loader";
import { AuthCtx } from "Contexts/index";
import appRoutes from "Providers/appRoutes";
import { useAppDispatch } from "Redux/helpers";
import { getUserOperatingSystem } from "Utils/funcs";

interface UserAuth {
    user?: {
        accessToken: string;
        tokenType: string;
        expiresIn: number;
    };
    error?: {
        message: string;
    };
}

const AuthProvider = ({ children }) => {
    const { i18n } = useTranslation();
    const [auth, setAuth] = useState({ authenticated: false, agent: {} });
    const [checkingToken, setCheckingToken] = useState(true);
    const history = useHistory();
    const tokenRef = useRef<string | undefined>(void 0);
    const tokenTypeRef = useRef<string | undefined>(void 0);
    const callBackURLRef = useRef<string | undefined>(void 0);
    const tokenExipresAtRef = useRef(0);
    const tokenRefreshTimoutRef = useRef<NodeJS.Timeout | number>(0);
    const onLoginSucceedHandlerRef = useRef<(user: UserAuth) => void>(function () {});
    const cleaners = useRef<() => void>(null);

    const onSaveTokenHandler = useCallback((userAuth: UserAuth) => {
        window.localStorage.setItem("id_token", userAuth.user?.accessToken);
        window.localStorage.setItem("token_type", userAuth.user?.tokenType);
    }, []);

    const onLogOutHandler = useCallback(async () => {
        try {
            clearTimeout(tokenRefreshTimoutRef.current as number);
            await Axios.post(`${process.env.APP_AUTH_SERVICE}logout`, null, {
                withCredentials: true,
            });
            setAuth({ authenticated: false, agent: {} });
            cleaners.current();
            localStorage.removeItem("id_token");
            history.push(appRoutes.login(i18n.language), { logout: true });
            return true;
        } catch (error) {
            console.error(error);
            return false;
        }
    }, [history]);

    onLoginSucceedHandlerRef.current = (userAuth: UserAuth) => {
        const { accessToken, tokenType, expiresIn } = userAuth.user;
        const userDetails = jwt(accessToken);
        tokenRef.current = accessToken;
        tokenTypeRef.current = tokenType;
        tokenExipresAtRef.current = expiresIn;
        //@ts-ignore
        userDetails.agentID = userDetails.id;
        delete (userDetails as any).exp;
        if (auth.authenticated && isEqual(auth.agent, userDetails)) {
            // Same user details no need to re-render
            return;
        }

        setAuth({
            authenticated: true,
            agent: userDetails,
            ...(userDetails as any),
        });
    };

    const onRefreshTokenHandler = useCallback(
        (expireAt: number) => {
            let numberOfRetry = 0;
            async function refresh() {
                try {
                    const { data: userAuth } = await Axios.post<UserAuth>(
                        `${process.env.APP_AUTH_SERVICE}refresh`,
                        null,
                        { withCredentials: true }
                    );

                    onLoginSucceedHandlerRef.current(userAuth);
                    onSaveTokenHandler(userAuth);
                    onRefreshTokenHandler(userAuth.user.expiresIn * 1000);
                } catch (error) {
                    numberOfRetry++;
                    console.error(error);
                    if (numberOfRetry > parseInt(process.env.MAX_REFRESH_TOKEN_ATTEMPS)) {
                        onLogOutHandler();
                        return;
                    }

                    // Retry again
                    tokenRefreshTimoutRef.current = setTimeout(refresh, 10000);
                }
            }
            tokenRefreshTimoutRef.current = setTimeout(refresh, expireAt - Date.now() - 5000);
        },
        [onLogOutHandler]
    );

    const onLoginHandler = useCallback(
        async (login: {
            username: string;
            password: string;
        }): Promise<{ token?: string; error?: { message: string } }> => {
            try {
                const osname = getUserOperatingSystem();
                const { data: userAuth } = await Axios.post<UserAuth>(
                    `${process.env.APP_AUTH_SERVICE}login`,
                    { ...login, osname },
                    {
                        withCredentials: true,
                        headers: {
                            "Content-Type": "application/x-www-form-urlencoded; charset=UTF-8",
                        },
                        validateStatus() {
                            return true;
                        },
                    }
                );
                if (typeof userAuth.error === "object") {
                    setAuth({ authenticated: false, agent: {} });
                    return { error: userAuth.error };
                }

                onLoginSucceedHandlerRef.current(userAuth);
                onSaveTokenHandler(userAuth);
                onRefreshTokenHandler(userAuth.user.expiresIn * 1000);
                return { token: `${userAuth.user.tokenType} ${userAuth.user.accessToken}` };
            } catch (e) {
                setAuth({ authenticated: false, agent: {} });
                return { error: { message: e.message } };
            }
        },
        [client]
    );

    const authenticateToken = useCallback(
        async (triggerAutoRefresh = false): Promise<boolean> => {
            try {
                if (triggerAutoRefresh) {
                    setCheckingToken(true);
                }

                const { data: userAuth } = await Axios.post<UserAuth>(
                    `${process.env.APP_AUTH_SERVICE}refresh`,
                    null,
                    { withCredentials: true }
                );

                onLoginSucceedHandlerRef.current(userAuth);
                onSaveTokenHandler(userAuth);
                if (triggerAutoRefresh) {
                    onRefreshTokenHandler(userAuth.user.expiresIn * 1000);
                }

                return true;
            } catch (error) {
                console.error(error);
                return false;
            } finally {
                if (triggerAutoRefresh) {
                    setCheckingToken(false);
                }
            }
        },
        [client]
    );

    const onSignupRedirectHandler = useCallback(
        async (token: string, tokenType: string): Promise<boolean> => {
            try {
                const details: any = jwt(token);
                tokenRef.current = token;
                tokenTypeRef.current = tokenType;
                tokenExipresAtRef.current = details?.exp;
                onRefreshTokenHandler(details?.exp * 1000);
                setAuth({
                    authenticated: true,
                    agent: details,
                    ...(details as any),
                });
                return true;
            } catch (error) {
                console.error(error);
                setAuth({ authenticated: false, agent: {} });
                return false;
            }
        },
        []
    );

    const token = useCallback(async (): Promise<string> => {
        // We have 4secondes or more in life the current token
        if (tokenExipresAtRef.current - Date.now() / 1000 >= 4) {
            return `${tokenTypeRef.current} ${tokenRef.current}`;
        }

        return new Promise((resolver) => {
            let cycleCount = 0;
            const tokenWaiter = function () {
                setTimeout(() => {
                    if (tokenExipresAtRef.current - Date.now() / 1000 >= 4) {
                        return resolver(`${tokenTypeRef.current} ${tokenRef.current}`);
                    }

                    cycleCount++;
                    if (cycleCount > 10) {
                        return resolver("");
                    }

                    tokenWaiter();
                }, 1000);
            };
        });
    }, []);

    const onAddOnLogoutClean = useCallback((cleanupFunction: () => void) => {
        cleaners.current = cleanupFunction;
    }, []);

    useEffect(() => {
        if (
            [
                appRoutes.login(),
                appRoutes.reset(),
                appRoutes.forgot(),
                appRoutes.confirm(),
                appRoutes.signup(),
            ].findIndex((route) =>
                new RegExp(route.replace(/\/$/, "")).test(window.location.pathname)
            ) === -1
        ) {
            callBackURLRef.current = window.location.href.replace(window.location.origin, "");
        }
    }, [window.location.href]);

    useEffect(() => {
        authenticateToken(true).then((response) => {
            if (response) {
                history.push(callBackURLRef.current || appRoutes.webRTC(i18n.language));
            }
        });
    }, []);

    useEffect(() => {
        const onLocalStorageChangeHandler = function () {
            const token = window.localStorage.getItem("id_token");
            const tokenType = window.localStorage.getItem("token_type");
            if (typeof token !== "string" || typeof tokenType !== "string") {
                onLogOutHandler();
            }
        };

        window.addEventListener("storage", onLocalStorageChangeHandler);
        return () => {
            window.removeEventListener("storage", onLocalStorageChangeHandler);
        };
    }, []);

    if (checkingToken) {
        return <Loader />;
    }
    return (
        <AuthCtx.Provider
            value={{
                ...auth,
                callbackURL: callBackURLRef.current,
                token,
                addOnLogoutClean: onAddOnLogoutClean,
                login: onLoginHandler,
                logout: onLogOutHandler,
                refreshTokenNow: authenticateToken,
                refreshToken: onRefreshTokenHandler,
                RedirectOnSingupCompletion: onSignupRedirectHandler,
            }}
        >
            {children}
        </AuthCtx.Provider>
    );
};

export default React.memo(AuthProvider);
