import React, {useEffect, useState} from 'react';
import { useRouter } from 'next/router';
import Link from 'next/link';
import Sticky from 'react-stickynode';
import MenuWrapper, { Menu, MenuItem, MenuLink } from "../../SideMenu/SideMenu.styled";
import {gql, useQuery} from "@apollo/client";

const menuItems = [
    {
        id: 1,
        label: 'Badge',
        path: '/uielements',
    },
    {
        id: 2,
        label: 'Page Title',
        path: '/uielements/page-title',
    },
    {
        id: 3,
        label: 'Widget Card',
        path: '/uielements/widget-card',
    },
    {
        id: 4,
        label: 'Carousel',
        path: '/uielements/carousel',
    },
    {
        id: 5,
        label: 'Cart Product',
        path: '/uielements/cart-product',
    },
    {
        id: 6,
        label: 'Instagram Card',
        path: '/uielements/instagram-card',
    },
    {
        id: 7,
        label: 'Product Card',
        path: '/uielements/product-card',
    },
    {
        id: 8,
        label: 'Pricing Card',
        path: '/uielements/pricing-card',
    },
    {
        id: 9,
        label: 'List Grid Card',
        path: '/uielements/listgrid-card',
    },
];

type MenuProps = {
    className?: string;
    onClick?: () => void;
};


const GET_CLASSROOMS = gql`
	query MyClassRoom($id: String!) {
  classRoomsTeacher(teacherID: $id) {
   title
    classRoomID
  }
}
`;


const ClassRooms = ({ className = '', onClick }: MenuProps) => {
    const { pathname } = useRouter();


    let classRooms = []

    const { data, loading, error, fetchMore } = useQuery<{ classRoomsTeacher: { title: string; classRoomID: string }[] }>(GET_CLASSROOMS, {
        variables: { id: '3cd7be2c-56c3-4c6b-bb55-76810decc9f7' },
        notifyOnNetworkStatusChange: true,
    });
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error: {error.message}</p>;
    if (!data) return <p>No data available</p>;

    const { classRoomsTeacher } = data;

     classRooms =  classRoomsTeacher







    return (
        <Sticky top=".navbar" innerZ="1">
            <MenuWrapper className="side-menu-wrapper">
                <Menu className={`side-menu ${className}`}>
                    {classRooms.map((item) => {
                        const menupathname = item['classRoomID']
                        const menupath = `/${menupathname[0]}`;


                        return (
                            <MenuItem key={`side-menu--key${item.classRoomID}`} onClick={onClick}>
                                <Link href={"/classrooms/"+item.classRoomID}>
                                    <MenuLink className={pathname === item.classRoomID ? 'active' : ''}>
                                        {item.title}
                                    </MenuLink>
                                </Link>
                            </MenuItem>
                        );
                    })}
                </Menu>
            </MenuWrapper>
        </Sticky>
    );
};

export default ClassRooms;
