// context/AuthContext.tsx
import { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { useRouter } from 'next/router';
import {jwtDecode,  JwtPayload } from 'jwt-decode';
import Cookies from 'js-cookie';

interface AuthContextType {
    user: JwtPayload | null;
    error: string | null;
    login: (requestBody: { username: string; password: string,osname:string }) => Promise<void>;
    logout: () => void;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

interface AuthProviderProps {
    children: ReactNode;
}

export const AuthProvider = ({ children }: AuthProviderProps) => {
    const [user, setUser] = useState<JwtPayload | null>(null);
    const [error, setError] = useState<string | null>(null);
    const router = useRouter();

    useEffect(() => {
        const token = localStorage.getItem('jwtToken');
        if (token) {
            const decoded = jwtDecode<JwtPayload>(token);
            setUser(decoded);
        }
    }, []);

    const login = async (requestBody: { username: string; password: string; osname:"linux"; }) => {
            const response = await fetch('http://127.0.0.1:3004/teacher-login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(requestBody),
            });

            if (response.ok) {
                const { user } = await response.json();
                Cookies.set('token', user.accessToken);
                localStorage.setItem('token', user.accessToken);
                const decoded = jwtDecode<JwtPayload>(user.accessToken);
                setUser(decoded);
                setError(null); // Clear any previous error
                router.push('/');
            } else {
                const errorData = await response.json();
                setError(errorData.error.message)
            }

    };

    const logout = () => {
        localStorage.removeItem('token');
        Cookies.remove('token');
        setUser(null);
        setError(null); // Clear any errors on logout
        router.push('/login');
    };

    return (
        <AuthContext.Provider value={{ user, error, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    const context = useContext(AuthContext);
    if (!context) {
        throw new Error('useAuth must be used within an AuthProvider');
    }
    return context;
};
