import React, {useEffect, useState} from 'react';
import { useRouter } from 'next/router';
import Link from 'next/link';
import Sticky from 'react-stickynode';
import MenuWrapper, { Menu, MenuItem, MenuLink } from "../../SideMenu/SideMenu.styled";
import {gql, useQuery} from "@apollo/client";


type MenuProps = {
    className?: string;
    onClick?: () => void;
};


const GET_Chapters = gql`
query Chapters($classRoomID: String!) {
  chapters(classRoomID: $classRoomID) {
   chapterID
    title
    arabicTitle
    description
  }
}

`;


const ClassRooms = ({ className = '', onClick, id }) => {
    const {query} = useRouter();



    let chapters = []

    const { data, loading, error, fetchMore } = useQuery<{ classRoomsTeacher: { title: string; classRoomID: string }[] }>(GET_Chapters, {
        variables: { classRoomID : id },
        notifyOnNetworkStatusChange: true,
    });
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error: {error.message}</p>;
    if (!data) return <p>No data available</p>;

    const { Chapters } = data;

    chapters =  Chapters







    return (
        <Sticky top=".navbar" innerZ="1">
            <MenuWrapper className="side-menu-wrapper">

            </MenuWrapper>
        </Sticky>
    );
};

export default ClassRooms;
