import React from "react";


interface AuthCtxProps {
    accountID: string;
    agentID: string;
    username: string;
    email: string;
    rolename: string;
    rolePersist: boolean;
    rolePredefined: boolean;
    authenticated: boolean;
    callbackURL: string;
    token: () => Promise<string>;
    logout: () => Promise<boolean>;
    addOnLogoutClean: (cleanupFunction: () => void) => void;
    login: (login: { username: string; password: string }) => Promise<{
        token?: string;
        error?: {
            message: string;
        };
    }>;
    refreshToken: (expireAt: number) => void;
    refreshTokenNow(): Promise<boolean>;
    RedirectOnSingupCompletion: (token: string, tokenType: string) => Promise<boolean>;
}

const Authctx = React.createContext<Partial<AuthCtxProps>>({});

export default Authctx;
