import React, {Fragment, useState} from 'react';
import { FiCheck } from 'react-icons/fi';
import { toast } from 'react-toastify';
import { NextPage } from 'next';
import Head from 'next/head';
import { useQuery, gql } from '@apollo/client';
import { useRouter } from 'next/router';
import { Button } from 'baseui/button';
import { Select } from 'baseui/select';
import { Input } from 'baseui/input';
import PageTitle from 'components/UiElements/PageTitle/PageTitle';
import Container from 'components/UiElements/Container/Container';
import Carousel, { Slide } from 'components/UiElements/Carousel/Carousel';
import useDirection from '../../utils/useDirection';
import { withApollo } from '../../apollo/client';

import Wrapper, {
	MediaWrapper,
	Image,
	ContentWrapper,
	List,
	ListItem,
	ProductTitle,
	ProductPrice,
	ProductDescription,
} from '../../components/PageStyles/Product.styled';
import { useCartDispatch } from '../../contexts/cart/cart.provider';
import {Block} from "baseui/block";
import {Cell, Grid} from "baseui/layout-grid";
import UiElementsMenu
	from "../../components/SideMenu/UiElementsMenu";
import Pricing
	from "../../components/UiElements/PricingCard/PricingCard";
import {
	StyledBodyCell,
	StyledTable
} from "baseui/table-grid";
import {
	StyledTableHeadAlt
} from "../../components/PageStyles/Apps.styled";
import {
	pricingCardPropsData
} from "../../data/uiElementsApiData";
import ClassRooms
	from "../../components/learning/classroom/ClassRoom";

const GET_CLASSROOM = gql`
query ClassRoom($classRoomID: String!) {
  classRoom(classRoomID: $classRoomID) {
 title
    studentCount
    price
    Rating
    image 
  }
}

`;

toast.configure();

const ProductSingle: NextPage<{}> = () => {
	const {
		query: { classRoomID },
	} = useRouter();

	console.log(classRoomID)
	const { data, loading, error } = useQuery(GET_CLASSROOM, {
		variables: {
			classRoomID
		},
		notifyOnNetworkStatusChange: true,
	});

	console.log(data)
	const dispatch = useCartDispatch();
	const [color, setColor] = useState<any>([
		{
			label: 'Black',
			id: 'black',
			code: '#000',
		},
	]);
	const [quantity, setQuantity] = useState('1');
	const [direction] = useDirection();
	console.log(color, quantity, 'color');

	if (!data) return null;
	const {
		title,
		studentCount,
		price,
		Rating,
		image,
	} =  data.classRoom;


	console.log(data, 'data');

	return (
		<>
			<Head>
				<title>{title} | 9ARINI.</title>
				<meta name="Description" content={title} />
			</Head>

			{/*<PageTitle*/}
			{/*	title={title}*/}
			{/*	subtitle="Awesome GitHub T-shirts and other cool swag"*/}
			{/*/>*/}

			<Container>
				<Block paddingTop={['0', '0', '0', '40px']}>
					<Grid gridColumns={12} gridGutters={0} gridMargins={0}>
						<Cell span={[12, 12, 3]}>
							<ClassRooms />
						</Cell>
						<Cell span={[12, 12, 9]}>
							<Block paddingTop={['10px', '15px', '30px', '0']}>
								<Block
									as="h2"
									paddingBottom="20px"
									overrides={{
										Block: {
											style: ({ $theme }) => {
												return {
													...$theme.typography.font650,
													color: $theme.colors.primaryA,
												};
											},
										},
									}}
								>
									PricingCard
								</Block>

								<Block paddingBottom="30px">
									<Grid gridColumns={12} gridGutters={0} gridMargins={0}>
										<Cell span={[12, 6]}>
											<Pricing
												title={title}
												type="Advance"
												items={[
													{
														id: 1,
														title: 'Access to MINST on spectrum',
													},
													{
														id: 2,
														title: 'Email support',
													},
													{
														id: 3,
														title: 'Support reply within 24hrs',
													},
												]}
												price="$799"
												btn="Get Started"
												isLoading={loading}

											/>
										</Cell>
									</Grid>
								</Block>

								<Block
									as="h2"
									paddingBottom="10px"
									overrides={{
										Block: {
											style: ({ $theme }) => {
												return {
													...$theme.typography.font650,
													color: $theme.colors.primaryA,
												};
											},
										},
									}}
								>
									API
								</Block>

								<Block
									as="p"
									paddingBottom="30px"
									overrides={{
										Block: {
											style: ({ $theme }) => {
												return {
													...$theme.typography.font200,
													color: $theme.colors.borderInverseOpaque,
												};
											},
										},
									}}
								>
									To get a customized PricingCard, set custom style in style
									prop.
								</Block>

								<Block
									overrides={{
										Block: {
											style: {
												minHeight: '150px',
											},
										},
									}}
								>
									<StyledTable $gridTemplateColumns="150px 250px auto auto auto">
										<StyledTableHeadAlt>Property</StyledTableHeadAlt>
										<StyledTableHeadAlt>Description</StyledTableHeadAlt>
										<StyledTableHeadAlt>Type</StyledTableHeadAlt>
										<StyledTableHeadAlt>Default</StyledTableHeadAlt>
										<StyledTableHeadAlt>Version</StyledTableHeadAlt>
										{pricingCardPropsData.map((item, index) => {
											const striped = index % 2 === 0;
											return (
												<Fragment key={index}>
													<StyledBodyCell $striped={striped}>
														{item.property}
													</StyledBodyCell>
													<StyledBodyCell
														$striped={striped}
														dangerouslySetInnerHTML={{
															__html: item.description,
														}}
													></StyledBodyCell>
													<StyledBodyCell $striped={striped}>
														<code>{item.type}</code>
													</StyledBodyCell>
													<StyledBodyCell $striped={striped}>
														{item.default}
													</StyledBodyCell>
													<StyledBodyCell $striped={striped}>
														{item.version && item.version}
													</StyledBodyCell>
												</Fragment>
											);
										})}
									</StyledTable>
								</Block>
							</Block>
						</Cell>
					</Grid>
				</Block>
			</Container>
		</>
	);
};

export default withApollo(ProductSingle);
