import React, {Fragment, useState} from 'react';
import { FiCheck } from 'react-icons/fi';
import { toast } from 'react-toastify';
import { NextPage } from 'next';
import Head from 'next/head';
import { useQuery, gql } from '@apollo/client';
import { useRouter } from 'next/router';
import { Button } from 'baseui/button';
import { Select } from 'baseui/select';
import { Input } from 'baseui/input';
import PageTitle from 'components/UiElements/PageTitle/PageTitle';
import Container from 'components/UiElements/Container/Container';
import Carousel, { Slide } from 'components/UiElements/Carousel/Carousel';
import useDirection from '../../utils/useDirection';
import { withApollo } from '../../apollo/client';

import Wrapper, {
	MediaWrapper,
	Image,
	ContentWrapper,
	List,
	ListItem,
	ProductTitle,
	ProductPrice,
	ProductDescription,
} from '../../components/PageStyles/Product.styled';
import { useCartDispatch } from '../../contexts/cart/cart.provider';
import {Block} from "baseui/block";
import {Cell, Grid} from "baseui/layout-grid";
import UiElementsMenu
	from "../../components/SideMenu/UiElementsMenu";
import Pricing
	from "../../components/UiElements/PricingCard/PricingCard";
import {
	StyledBodyCell,
	StyledTable
} from "baseui/table-grid";
import {
	StyledTableHeadAlt
} from "../../components/PageStyles/Apps.styled";
import {
	pricingCardPropsData
} from "../../data/uiElementsApiData";
import ClassRooms
	from "../../components/learning/classroom/ClassRoom";
import LoginForm
	from "../../containers/HookForm/LoginForm";
import {
	AvatarWrapper,
	ButtonGroup
} from "../../components/PageStyles/Settings.styled";
import {FormControl} from "baseui/form-control";
import dynamic from "next/dynamic";

const GET_CLASSROOM = gql`
query ClassRoom($classRoomID: String!) {
  classRoom(classRoomID: $classRoomID) {
    title
    studentCount
    price
    Rating
    image 
    badge
  }
}

`;

toast.configure();

const ProductSingle: NextPage<{}> = () => {
	const {
		query: { classRoomID },
	} = useRouter();

	console.log(classRoomID)
	const { data, loading, error } = useQuery(GET_CLASSROOM, {
		variables: {
			classRoomID
		},
		notifyOnNetworkStatusChange: true,
	});

	console.log(data)




	if (!data) return null;
	const {
		title,
		studentCount,
		price,
		Rating,
		image,
		badge,
	} =  data.classRoom;


	console.log(data, 'data');

	const AvatarWithNoSSR = dynamic(() => import('react-avatar-edit'), {
		ssr: false,
	});

	return (
		<>
			<Head>
				<title>{title} | 9ARINI.</title>
				<meta name="Description" content={title} />
			</Head>

			{/*<PageTitle*/}
			{/*	title={title}*/}
			{/*	subtitle="Awesome GitHub T-shirts and other cool swag"*/}
			{/*/>*/}

			<Container>
				<Block paddingTop={['0', '0', '0', '40px']}>
					<Grid gridColumns={12} gridGutters={0} gridMargins={0}>
						<Cell span={[12, 12, 3]}>
							<ClassRooms />
						</Cell>
						<Cell span={[12, 12, 9]}>
							<Block paddingTop={['10px', '20px', '30px', '0']}>
								<form onSubmit={()=>{}}>
									<Block marginBottom="30px">
										<AvatarWrapper>
											<AvatarWithNoSSR
												label="Choose avatar"
												labelStyle={{
													fontSize: '16px',
													fontWeight: 500,
													cursor: 'pointer',
												}}
												width={150}
												height={150}
												onCrop={handleOnCrop}
												onBeforeFileLoad={handleBeforeFileLoad}
												src={require('../../assets/images/avatar.jpg')}
											/>
										</AvatarWrapper>
									</Block>
									<Block marginBottom="30px">
										<FormControl
											label="Your Name"
											caption="Please use 32 characters at maximum"
											error={true && 'This field is required'}
											overrides={{
												Label: {
													style: ({ $theme }) => {
														return { ...$theme.typography.font350 };
													},
												},
											}}
										>
											<Input
												name="name"
												autoComplete="off"
												inputRef={register({ required: true, maxLength: 32 })}
												placeholder="Please enter your full name or a display name you comfortable with"
												overrides={{
													InputContainer: {
														style: () => {
															return { backgroundColor: 'transparent' };
														},
													},
												}}
											/>
										</FormControl>
									</Block>

									<Block marginBottom="30px">
										<FormControl
											label="Your User Name"
											caption="Please use 16 characters at maximum"
											error={errors.username && 'This field is required'}
											overrides={{
												Label: {
													style: ({ $theme }) => {
														return { ...$theme.typography.font350 };
													},
												},
											}}
										>
											<Input
												name="username"
												autoComplete="off"
												placeholder="Please enter your user name or a display name you comfortable with"
												inputRef={register({ required: true, maxLength: 16 })}
												overrides={{
													InputContainer: {
														style: () => {
															return { backgroundColor: 'transparent' };
														},
													},
												}}
											/>
										</FormControl>
									</Block>

									<Block marginBottom="30px">
										<FormControl
											label="Your Email"
											caption="jennydoe@example.io"
											error={errors.email && 'This field is required'}
											overrides={{
												Label: {
													style: ({ $theme }) => {
														return { ...$theme.typography.font350 };
													},
												},
											}}
										>
											<Input
												type="email"
												name="email"
												autoComplete="off"
												inputRef={register({
													required: true,
													pattern: /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/,
												})}
												placeholder="Please enter your email address"
												overrides={{
													InputContainer: {
														style: () => {
															return { backgroundColor: 'transparent' };
														},
													},
												}}
											/>
										</FormControl>
									</Block>

									<ButtonGroup>
										<Button
											type="submit"
											size="large"
											overrides={{
												BaseButton: {
													style: ({ $theme }) => {
														return {
															width: '131px',
															...$theme.typography.font250,
														};
													},
												},
											}}
										>
											Save
										</Button>
										<Button
											type="reset"
											onClick={() => reset()}
											kind="tertiary"
											size="large"
											overrides={{
												BaseButton: {
													style: ({ $theme }) => {
														return {
															width: '131px',
															...$theme.typography.font250,
														};
													},
												},
											}}
										>
											Cancel
										</Button>
									</ButtonGroup>
								</form>
							</Block>
						</Cell>
					</Grid>
				</Block>
			</Container>
		</>
	);
};

export default withApollo(ProductSingle);
