import React, {useEffect} from "react";

import {useAuth} from "./AuthContext";
import {useRouter} from "next/router";


const PrivateRoute = ({children}) => {
  const {user} = useAuth()
 const router =  useRouter()

    useEffect(() => {
        if (user == null) {
            router.push('login')
        }
    }, []);

  return <>{children}</>
};

export default PrivateRoute;
