import React, {
	Fragment,
	useEffect,
	useState
} from 'react';
import { FiCheck } from 'react-icons/fi';
import { toast } from 'react-toastify';
import { NextPage } from 'next';
import Head from 'next/head';
import { useQuery, gql } from '@apollo/client';
import { useRouter } from 'next/router';
import { Button } from 'baseui/button';
import { Select } from 'baseui/select';
import { Input } from 'baseui/input';
import PageTitle from 'components/UiElements/PageTitle/PageTitle';
import Container from 'components/UiElements/Container/Container';
import Carousel, { Slide } from 'components/UiElements/Carousel/Carousel';
import useDirection from '../../../utils/useDirection';
import { withApollo } from '../../../apollo/client';

import Wrapper, {
	MediaWrapper,
	Image,
	ContentWrapper,
	List,
	ListItem,
	ProductTitle,
	ProductPrice,
	ProductDescription,
} from '../../../components/PageStyles/Product.styled';
import { useCartDispatch } from '../../../contexts/cart/cart.provider';
import {Block} from "baseui/block";
import {Cell, Grid} from "baseui/layout-grid";
import UiElementsMenu
	from "../../../components/SideMenu/UiElementsMenu";
import Pricing
	from "../../../components/UiElements/PricingCard/PricingCard";
import {
	StyledBodyCell,
	StyledTable
} from "baseui/table-grid";
import {
	StyledTableHeadAlt
} from "../../../components/PageStyles/Apps.styled";
import {
	pricingCardPropsData
} from "../../../data/uiElementsApiData";
import ClassRooms
	from "../../../components/learning/classroom/ClassRoom";
import LoginForm
	from "../../../containers/HookForm/LoginForm";
import {
	AvatarWrapper,
	ButtonGroup
} from "../../../components/PageStyles/Settings.styled";
import {FormControl} from "baseui/form-control";
import dynamic from "next/dynamic";
import {useForm} from "react-hook-form";


const AvatarWithNoSSR = dynamic(() => import('react-avatar-edit'), {
	ssr: false,
});

type FormData = {
	avatar: string;
	name: string;
	username: string;
	email: string;
};


const GET_CLASSROOM = gql`
query ClassRoom($classRoomID: String!) {
  classRoom(classRoomID: $classRoomID) {
    title
    studentCount
    price
    Rating
    image 
    badge
  }
}

`;

toast.configure();

const ProductSingle: NextPage<{}> = () => {
	const {
		query: { classRoomID },
	} = useRouter();

	console.log(classRoomID)
	const { data, loading, error } = useQuery(GET_CLASSROOM, {
		variables: {
			classRoomID
		},
		notifyOnNetworkStatusChange: true,
	});

	console.log(data)




	if (!data) return null;
	const {
		title,
		studentCount,
		price,
		Rating,
		image,
		badge,
	} =  data.classRoom;


	console.log(data, 'data');

//form





	const handleBeforeFileLoad = (e: any) => {
		if (e.target.files[0].size > 71680) {
			alert('File is too big!');
			e.target.value = '';
		}
	};

	const router = useRouter()
	const onSubmit = (data: any) => {
		router.push("/classroom/{uuid}/detail")
	};
	//endform

	return (
		<>
			<Head>
				<title>{title} | 9ARINI.</title>
				<meta name="Description" content={title} />
			</Head>

			<Container>
				<Block paddingTop={['0', '0', '0', '40px']}>
					<Grid gridColumns={12} gridGutters={0} gridMargins={0}>
						<Cell span={[12, 12, 3]}>
							{/* Assuming ClassRooms component here */}
							<ClassRooms />
						</Cell>
						<Cell span={[12, 12, 9]}>
							<Block paddingTop={['10px', '20px', '30px', '0']}>
								<form onSubmit={handleSubmit(onSubmit)}>
									<Block marginBottom="30px">
										{/* Avatar selection */}
										<AvatarWrapper>
											<AvatarWithNoSSR
												label="Choose avatar"
												labelStyle={{
													fontSize: '16px',
													fontWeight: 500,
													cursor: 'pointer',
												}}
												width={150}
												height={150}
												onCrop={handleOnCrop}
												onBeforeFileLoad={handleBeforeFileLoad}
												src={image}
											/>
										</AvatarWrapper>
									</Block>

									<Block marginBottom="30px">
										{/* Title input */}
										<FormControl
											label="ClassRoom Title"
											error={errors.name && 'This field is required'}
											overrides={{
												Label: {
													style: ({ $theme }) => ({
														...$theme.typography.font350,
													}),
												},
											}}
										>
											<Input
												name="name"
												value={title}
												onChange={(e) => setTitle(e.target.value)}
												autoComplete="off"
												inputRef={register({ required: true, maxLength: 32 })}
												placeholder="Enter the classroom title"
												overrides={{
													InputContainer: {
														style: () => ({
															backgroundColor: 'transparent',
														}),
													},
												}}
											/>
										</FormControl>
									</Block>

									<Block marginBottom="30px">
										{/* Student count input */}
										<FormControl
											label="Students Enrolled"
											error={errors.studentCount && 'This field is required'}
											overrides={{
												Label: {
													style: ({ $theme }) => ({
														...$theme.typography.font350,
													}),
												},
											}}
										>
											<Input
												type="text"
												name="studentCount"
												disabled={true}
												value={studentCount + " students"}

											/>
										</FormControl>
									</Block>

									<Block marginBottom="30px">
										{/* Price input */}
										<FormControl
											label="Price"
											disabled={true}
										>
											<Input
												type="text"
												name="price"
												value={price + " DA/mo"}
											/>
										</FormControl>
									</Block>

									<Block marginBottom="30px">
										{/* Rating input */}
										<FormControl
											label="Rating"
											overrides={{
												Label: {
													style: ({ $theme }) => ({
														...$theme.typography.font350,
													}),
												},
											}}
										>
											<Input
												type="text"
												name="rating"
												value={Rating.toString().slice(0,4) + " of 5"}
												disabled={true}
												overrides={{
													InputContainer: {
														style: () => ({
															backgroundColor: 'transparent',
														}),
													},
												}}
											/>
										</FormControl>
									</Block>

									<Block marginBottom="30px">
										{/* Badge selection */}
										<FormControl
											label="Badge"
											disabled={true}
											overrides={{
												Label: {
													style: ({ $theme }) => ({
														...$theme.typography.font350,
													}),
												},
											}}
										>
											<Input
												name="badge"
												value={badge}
												onChange={(e) => setBadge(e.target.value)}
												autoComplete="off"
												placeholder="Enter the badge"
												inputRef={register({ required: true, maxLength: 16 })}
												overrides={{
													InputContainer: {
														style: () => ({
															backgroundColor: 'transparent',
														}),
													},
												}}
											/>
										</FormControl>
									</Block>

									<ButtonGroup>
										<Button
											type="submit"
											size="large"
											overrides={{
												BaseButton: {
													style: ({ $theme }) => ({
														width: '131px',
														...$theme.typography.font250,
													}),
												},
											}}
										>
											Content
										</Button>
										<Button
											type="reset"
											onClick={() => reset()}
											kind="tertiary"
											size="large"
											overrides={{
												BaseButton: {
													style: ({ $theme }) => ({
														width: '131px',
														...$theme.typography.font250,
													}),
												},
											}}
										>
											back
										</Button>
									</ButtonGroup>
								</form>
							</Block>
						</Cell>
					</Grid>
				</Block>
			</Container>
		</>
	);
};

export default withApollo(ProductSingle);
