import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { FormControl } from 'baseui/form-control';
import { Input } from 'baseui/input';
import { Button } from 'baseui/button';
import { Block } from 'baseui/block';
import { useQuery, gql } from '@apollo/client';
import { withApollo } from 'apollo/client';
type Props = any;

const GET_DASHBOARD = gql`
	query {
		dashboard {
			recentApps {
				id
				name
				image
				description
			}
			productViews {
				categories
				products
				views
			}
			productsBar {
				labels
				products
			}
			cashFlow {
				categories
				cash
			}
		}
	}
`;


const LoginForm: React.FC<Props> = props => {
  const { register, handleSubmit, setValue, errors } = useForm();



  const { data, loading, error, fetchMore } = useQuery(GET_DASHBOARD, {
		notifyOnNetworkStatusChange: true,
	});
	if (!data) return null;
	const { productViews, recentApps, productsBar, cashFlow } = data.dashboard;


  useEffect(() => {
    register({ name: 'email' });
    register({ name: 'password' });

  }, [register]);

  const onSubmit = data => {
    console.log(data);
    alert(JSON.stringify(data, null, 4));
  };






  return (
    <form onSubmit={handleSubmit(onSubmit)} style={{ width: '100%' }}>


      <Block marginBottom="30px">
        <FormControl
          label="Your Email"
          caption="johndoe@demo.io"
          error={errors.email && 'Please enter a valid email address'}
        >
          <Input
            name="email"
            inputRef={register({
              required: true,
              pattern: /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/,
            })}
            overrides={{
              InputContainer: {
                style: () => {
                  return { backgroundColor: 'transparent' };
                },
              },
            }}
          />
        </FormControl>
      </Block>

      <Block marginBottom="30px">
        <FormControl
          label="Password"
          caption="********"
          error={errors.name && 'This field is required'}
        >
          <Input
            type="password"
            name="password"
            inputRef={register({ required: true, maxLength: 32})}
            overrides={{
              InputContainer: {
                style: () => {
                  return { backgroundColor: 'transparent' };
                },
              },
            }}
          />
        </FormControl>
      </Block>

      <Button type="submit">Login</Button>
    </form>
  );
};

export default LoginForm;
